import React, { Component } from "react";
import Country from "./Country"
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      countries: []
    }
  }

  componentDidMount() {
    fetch("https://restcountries.eu/rest/v2/all")
      .then(res => res.json())
      .then(jsonRes => {
        console.log('====================================')
        console.log(jsonRes)
        console.log('====================================')
        this.setState({countries: jsonRes})
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Countries of the World</h1>
        {
          this.state.countries.map (country => {
            return (
              <Country />
            )
          })}
      </div>
    );
  }
}

export default App;
