import React, { Component } from 'react';
import Stopwatch from "./Stopwatch";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      secondsElapsed: 40
    }
  }

  getSeconds = () => {
    return ('0' + this.state.secondsElapsed % 60).slice(-2)
  }

  getMinutes = () => {
    return Math.floor(this.state.secondsElapsed / 60)
  }

  startCount = () => {
    this.secondsElapsed = setInterval(() => this.setState({
      secondsElapsed: this.state.secondsElapsed +1
    }), 1000);
  }

  pauseCount = () => {
    console.log('paused')
    clearInterval(this.secondsElapsed)
  }

  resetCount = () => {
    clearInterval(this.secondsElapsed)
    this.setState({secondsElapsed: 0})
  }

  render() {
    return (
      <Stopwatch minutes={this.getMinutes} seconds={this.getSeconds} start={this.startCount} pause={this.pauseCount} />
    );
  }
}

export default App;
