import React, { Component } from 'react';
import Stopwatch from "./Stopwatch";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      secondsElapsed: 5
    }
  }

  getSeconds() {
    currentSeconds = '0' + this.state.secondsElapsed % 60
    return currentSeconds.splice(-2)
  }





  render() {
    return (
      <Stopwatch setCount={this.getSeconds()}/>
    );
  }
}

export default App;
