import React, { useState } from "react"
//  display the
// capital, currency code, and the name of the first language below the country
// name.
const Country = ({ name }) => {
  const [capital, setCapital] = useState('')
  const [currencyCode, setCurrencyCode] = useState('')
  const [language, setLanguage] = useState('')
  const handler = () => {
    // fetching name
    fetch(`https://restcountries.eu/rest/v2/name/${name}`)
      .then(res => res.json())
      .then(countryResponse => {
        console.log(countryResponse);
        const { capital, currencies, languages } = countryResponse[0];
        let currencyCode = currencies[0].code;
        let language = languages[0].name
        console.log(capital, currencyCode, language);
        setCapital(capital)
        setCurrencyCode(currencyCode)
        setLanguage(language)
    })
  }
  return (
    <div onClick={handler} style={{
      fontSize: '16px',
      padding: '8px',
      border: '2px solid black',
      marginBottom: '12px'
    }}>
      {name}
{currencyCode && (<div>
        <p>
        Capital: {capital}
        </p>
        <p>
        Currency Code: {currencyCode}
        </p>
        <p>
        Language: {language}
        </p>
      </div>)}
    </div>
  )
}
export default Country
