import React, { Component } from "react";
import Header from "../Header/Header.js"
import Slider from "../Slider/Slider.js"
import Content from "../Content/Content.js";
import Footer from "../Footer/Footer"
import "./style.css";

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      pictures: [],
      imageUrl: []
    }
  }

  componentDidMount() {
    const API_KEY = process.env.REACT_APP_API_KEY 
    const url = `https://api.unsplash.com/search/photos?page=1&per_page=30/&client_id=${API_KEY}&query=popular`
    fetch(url)
      .then(res => res.json())
      .then(jsonRes => {
        console.log('==========================')
        console.log(jsonRes)
        console.log('==========================')
        this.setState({ pictures: jsonRes.results, imageUrl: jsonRes.results[0].urls.full })
      })
      .catch(err => {
        console.log('Something went wrong!')
      })
  }

  nextImage = () => {
    let i = 0 
    i++
    this.setState({ imageUrl: this.state.pictures[i].urls.full })

    if (i === 29) {
      i = 0
    }
    console.log(i)
  }

  previousImage = () => {
    this.state.imageUrl = []
    let number = 0
    this.state.imageUrl = this.state.pictures.results[number - 1].urls.full
  }

  render() {
    return (
      <div id="body">
        <Header />
        <Slider next={this.nextImage} previous={this.previousImage} link={this.state.imageUrl} />
        <div id="content">
        {
          this.state.pictures.map (picture => {
            return (
              <Content key={picture.id} link={picture.urls.full} />
            )
          })}
        </div>
        <Footer />
      </div>
    );
  }
}

export default App;
