import React, { useState } from 'react';
import "../Slider/slider.css"

export default function Slider() {
    const [picture, setPicture ] = useState('')
    const [link, setLink] = useState('')
    const handler = () => {
      // fetching name
        const API_KEY = process.env.REACT_APP_API_KEY 
        const url = `https://api.unsplash.com/search/photos?page=1&per_page=30/&client_id=${API_KEY}&query=popular`
        fetch(url)
          .then(res => res.json())
          .then(sliderPics => {
            console.log('==========================')
            console.log(sliderPics)
            console.log('==========================')
          let pictures = sliderPics.results;
          let links = sliderPics.results[0].urls.full
          setPicture(pictures)
          setLink(links)
      })
    }

    return (
        <div onClick={handler} id="slider">
            <img src={link} alt="" className="test123" />
        </div>
    )
}


