import React, { Component } from 'react';
import Stopwatch from "./Stopwatch";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      secondsElapsed: 0
    }
  }

  getSeconds = () => {
    return (this.state.secondsElapsed % 60)
  }





  render() {
    return (
      <Stopwatch setCount={this.state.getSeconds}/>
    );
  }
}

export default App;
