import React from "react"

const Country = ({ name }) => {
    const handler = () => {
        console.log('handling: ' + name)
        fetch(`https://restcountries.eu/rest/v2/name/${name}`)
            .them(res => res.json())
            .then(countryRes => {
                const { capital, currencies, languages } = countryRes[0]
                let currencyCode = currencies[0].code
                let language = languages[0].name
            console.log(capital, currencies, languages)
        })
    }
    return (
        <div onClick={handler} style={{
            fontSize: '16px',
            padding: '8px',
            border: '2px solid black',
            margin: '12px'
        }}>{name}</div>
    )
}

export default Country
