import React, { Component } from 'react';
import Stopwatch from "./Stopwatch";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      count: 40
    }
  }

  getSeconds = () => {
    return ('0' + this.count % 60).slice(-2)
  }

  getMinutes = () => {
    return Math.floor(this.state.count / 60)
  }

  startCount = () => {
    this.count = setInterval(() => this.setState({
      secondsElapsed: this.state.count +1
    }), 1000);
    console.log('starting')
  }

  pauseCount = () => {
    console.log('paused')
    clearInterval(this.count)
  }

  resetCount = () => {
    let oldState = this.state.count
    this.setState({ count: oldState = 0 })
    console.log('reset')
  }

  render() {
    return (
      <Stopwatch count={this.state.count} minutes={this.getMinutes} seconds={this.getSeconds} start={this.startCount} pause={this.pauseCount} reset={this.resetCount} />
    );
  }
}

export default App;
