import React, { useState } from 'react';
import "../Slider/slider.css"

export default function Slider() {

    let slides = null
    const handler = () => {
        const API_KEY = process.env.REACT_APP_API_KEY 
        const url = `https://api.unsplash.com/search/photos?page=1&per_page=30/&client_id=${API_KEY}&query=popular`
        fetch(url)
          .then(res => res.json())
          .then(sliderPics => {
            console.log('==========================')
            console.log(sliderPics)
            console.log('==========================')
              slides = sliderPics.results
              buildSlides()
          })
          .catch(err => {
            console.log('Something went wrong!')
          })
    }

    buildSlides = () => {
        let number = 0

        display = slides[slides.length - 1].urls.full

        setInterval(() => {
            display = slides[number].urls.full
            number + 1

            if (number === slides.length) {
                number = 0
            }
        }, 10000)
    }

    return (
        <div onClick={handler} id="slider">
            <img src={display} alt="" />
        </div>
    )
}
