import React, { Component } from 'react';
import Stopwatch from "./Stopwatch";
import './App.css';

function App() {
  const [count, setCount] = useState(0)
  const startCount = () => {
    // count to go up by 1
    console.log('start');
    setCount(count + 1)
  }

  const stopCount = () => {
    // count to go down by 1
    console.log('stop');
    setCount(count - 1)
  }

  const resetCount = () => {
    // reset
    console.log('Reset')
  }

  return (
      <Stopwatch />
  );
}

export default App;
