import React, {useState} from 'react';
import "./Stopwatch.css"

export default function Stopwatch(props) {
    return (
      <div className="stopwatch">
        <h1>{props.number}</h1>
        <div className="controls">
          <button onClick={props.startCounter}>Reset</button>
          <button onClick={props.stopCount}>Start</button>
          <button onClick={props.resetCount}>Pause</button>
        </div>
      </div>
    );
  }
